var Dispatcher = require('boldlygo/utils/flux.dispatcher');

module.exports = {

	setupGame : function(initialGameConfig){
		Dispatcher.dispatch({
			type : 'SETUP_GAME',
			payload : initialGameConfig
		})
	},

	updateBuilding : function(buildingId, newState){
		Dispatcher.dispatch({
			type : 'UPDATE_BUILDING',
			payload : {
				id : buildingId,
				newState : newState
			}
		})
	},

	newEvent : function(){
		Dispatcher.dispatch({
			type : 'NEW_EVENT'
		})
	},

	nextPhase : function(){
		Dispatcher.dispatch({
			type : 'NEXT_PHASE'
		})
	},

	nextRound : function(){
		Dispatcher.dispatch({
			type : 'NEXT_ROUND'
		})
	},

};