/** @jsx React.DOM */
var React = require('react/addons');
var _ = require('lodash');

var polyfills= require('boldlygo/polyfills');

var List = React.createClass({
	render : function(){
		var self = this;
		return(
			<ul {...this.props} >
				{_.reduce(this.props.items, function(r, item){
					if(!item) return r;
					r.push(<li>{item}</li>)
					return r;
				}, [])}
			</ul>
		);
	}
});

module.exports = List;